def hamming_distance(sequence_s, sequence_t):
   #if len(sequence_s) != sequence_t:
       #raise ValueError("Input strings must have the same length")
        
    distance = 0
   
    for i in range(len(sequence_s)):
        if sequence_s[i] != sequence_t[i]:
            distance += 1
        
    return distance   

sequence_s = ''
sequence_t = ''


hamming_dist = hamming_distance(sequence_s, sequence_t)
print (hamming_dist)